INSERT INTO products (opt_price, price, id, country_producer, on_stock, photo_url, producer, product_description, title) VALUES (null, 75.00, 154, null, 'ON_STOCK', 'https://images.prom.ua/j90XmIO0UUBgAWhWcozHtngZZEvcdV3Lytr3krkDm_k=/3049481999_skinman-soft-skinman.jpg', 'ECOLAB', 'Засіб для гігієнічного миття рук «Скінман Софт», флакон 100 мл Skinman Soft являє собою спиртовий розчин готовий до застосування, призначений для гігієнічної та хірургічної дезінфекції шкіри рук. Засіб являє собою комбінацію 3-х активних діючих речовин (ізопропіловий спирт, бензалконій хлорид, ундециленова кислота) з комплексом для догляду за шкірою рук. Володіє доведеними бактерицидними (включаючи мікобактерії туберкульозу), противірусними та фунгіцидними властивостями. Додатковий догляд за шкірою рук. Переваги: Містить 3 активних компоненти: ізопропіловий спирт, ЧАС (четвертинні амоніевие сполуки), ундециленова кислота - надійність і пролонгований ефект. Містить гліцерин - додатковий догляд за руками. Значення рH = 4,5-5,5 - не сушить шкіру рук. Приємний запах. Сфера використання: ЛПЗ, салони краси, перукарні, косметологічні кабінети, навчальні та соціальні заклади, промислові підприємства, заклади громадського харчування, торгівлі, транспорту, комунальних об''єктів і т.д. Склад: Ізопропіловий спирт - 60%, бензалконію хлорид - 0,15%, ундециленова кислота - 0,1%', 'Скінман Софт (Skinman Soft) 100мл, ECOLAB');
INSERT INTO products (opt_price, price, id, country_producer, on_stock, photo_url, producer, product_description, title) VALUES (null, 1499.00, 137, null, 'ON_STOCK', 'https://images.prom.ua/D1GXAiVlfmXzuQrsd5_gZRqcKT78XfF1eWMliMHMUnw=/2986705831_sekusept-aktiv-sekusept.jpg', 'ECOLAB', 'Секусепт Актив Sekusept Aktiv є комплексним засобом, який поєднує в собі ефекти активного очищення, дезінфекції, та хімічні стерилізацію. Переваги Секусепт Актив: Засіб на основі надоцтової кислоти і активного кисню. Швидке і активне очищення інструментів з подальшою дезінфекцією і можливістю стерилізації. Являє собою порошок: не утворює піни, добре розчиняється, не порошить. Не має їдкого запаху. Діючі речовини екологічно безпечні. Наявність рекомендацій від виробників медичного обладнання: Olympus, Fujinon, Karl Storz. Володіє доведеними бактерицидними (включаючи мікобактерії туберкульозу), противірусними, фунгіцидними та спороцидними властивостями. Сфера використання Секусепт Актив: достерилізаційна очитска, дезінфекція і стерилізація інструментів у ЛПЗ, лабораторного посуду та іструментыв на підприємствах фармацевтичної, мікробіологічної та комунально-побутової сфери (косметологічні кабінети, перукарні тощо). Склад Секусепт Актив: пероксигідрат карбонату натрію - 44,55%, тетраацетилетилендіамін - 25,0% Цей товар ви можете купити/купить у нас на сайті або замовивши за телефоном +380957913420', 'Секусепт Актив Sekusept Activ 1,5кг ECOLAB');
