INSERT INTO products (opt_price, price, id, country_producer, on_stock, photo_url, producer, product_description, title) VALUES (null, 360.00, 157, null, 'ON_STOCK', 'https://images.prom.ua/XN_GekHBxvsZPZPdC3iss2Ej4bRSn0h8uPTrbKAlZmw=/3051845142_skinman-soft-protekt.jpg', 'ECOLAB', 'Спиртовий антисептик для рук «Скінман Софт Протект», картридж 750 мл Призначений для дезінфекції рук для частого використання. Забезпечує мікробіологічну чистоту рук без подразнення шкіри. Унікальна формула гарантує негайну дезінфекцію і тривалий захист шкіри. Він забезпечує високий рівень чистоти в лікарні і в медичних установах, включаючи відділення, лікарні та операційні. Він володіє повною вирулицидной активністю, тому його можна використовувати у разі епідемії або ситуації високого ризику. Збагачені інгредієнтами, такими як вітамін Е, пантенол і гліцерин, які підтримують шкіру рук в дуже хорошому стані. Переваги: делікатна рідка формула, що не залишає липких рук швидке дію і знищення всіх вірусів, бактерій, мікобактерій туберкульозу, дріжджів та грибів забезпечує мікробіологічну чистоту рук призначений для гігієнічної та хірургічної дезінфекції рук дуже добре вбирається, не відчуваючи мокрих рук містить речовини для догляду - вітамін Е, пантенол і гліцерин, які роблять гель надзвичайно делікатним для рук навіть при частому використанні і додатково доглядають за ними Сфера застосування: для лікарів, медсестер та медичного персоналу під час процедур, операцій і в будь-якій ситуації, коли необхідно мати мікробіологічно чисті руки для лікарів та медичного персоналу в діагностичних лабораторіях і лабораторіях для пацієнтів та відвідувачів, які знаходяться в лікарні або медичному центрі для опікунів, мешканців та відвідувачів будинків престарілих та хоспісів у разі необхідності вилікувати руки для косметологів, перукарів, татуювальників перед косметичними процедурами для дорослих і дітей, яким необхідно мати мікробіологічно чисті руки Спектр діяльності: 20 секунд - EN 1500 Гігієнічна дезі