INSERT INTO products (opt_price, price, id, country_producer, on_stock, photo_url, producer, product_description, title) VALUES (null, 3700.00, 14, null, 'ABSENT', 'https://images.prom.ua/lPE08JGOuaOTJ2Fhes0G4Czy3_UJ6w4TJScaembanuw=/3022961623_aniozim-xl3-5l.jpg', 'ANIOS', 'Засіб на основі інноваційної формули. Сучасна комбінація активно діючих, миючих речовин нового покоління і трьохензимного комплексу. Призначений для дезінфекції, достерилізаційного очищення (у т. ч. поєднаних в одному процесі), дезінфкції високого рівня виробів медичного призначення. Відмінна сумісність з широким спектром матеріалів, включаючи матеріали з яких виготовлені ендоскопічна апаратура, мікрохірургічні інструменти та вироби з оптикою. Руйнує біоплівки за 5 хвилин. Придатне для використання в УЗ-ваннах, автоматичних мийних машинах. Стабільність робочих розчинів - 7 діб.', 'Аніозим XL3 5л, Anios');
INSERT INTO products (opt_price, price, id, country_producer, on_stock, photo_url, producer, product_description, title) VALUES (null, 245.00, 214, null, 'ABSENT', 'https://images.prom.ua/sDWzF6jwzvXWXJ45yq_JqoW-ITkcUrL3Iz1oqGozuos=/4622171085_vindez-chas-am-1l.jpg', 'Ама', '«Віндез ЧАС-АМ» – дезінфекційний засіб широкого спектра дії. Форма випуску: концентрований засіб у формі прозорої рідини, від безбарвної до світло-жовтого кольору, яка добре змішується з водою у будь-яких співвідношеннях. Склад засобу: N,N-біс(3-амінопропил)додециламін 10,0%, алкілдиметилбензиламонію хлорид 6,5%; поверхнево-активні речовини, інгібітор корозії, регулятор pH, стабілізатор, комплексоутворювач, вода до 100,0%. Антимікробна складова: 6,5% четвертинної амонієвої сполуки, 10% триаміну. Властивості: водні розчини засобу прозорі, майже безбарвні, майже не мають запаху, стійкі; забезпечують ефективну дезінфекцію широкого спектра об’єктів; мають пролонговану антимікробну дію, відмінні змочувальні та миючі властивості, усувають неприємні запахи; не пошкоджують об’єкти обробки; не фіксують органічні та неорганічні забруднення на об’єктах обробки; добре змиваються, не залишають плям і нальоту; гомогенізують мокротиння та інші виділення. Засіб незаймистий, вибухобезпечний. Призначення: • профілактична, поточна та заключна дезінфекція, поточні та генеральні прибирання в установах, організаціях і на підприємствах різного профілю, а також у побуті; • дезінфекція поверхонь приміщень, повітря у приміщеннях, меблів, медичного обладнання (у т.ч. особливо чутливої апаратури, кувезів), технологічного обладнання, санітарно-технічного обладнання, транспортних засобів; • дезінфекція кухонного та столового посуду, ємностей для води, систем подачі води; • дезінфекція білизни, одягу, взуття, іграшок, спортивного інвентарю, прибирального інвентарю; • дезінфекція предметів догляду хворих, засобів особистої гігієни; • дезінфекція, достерилізаційне очищення, стерилізація виробів медичного призначення (у т.ч. гну