INSERT INTO products (opt_price, price, id, country_producer, on_stock, photo_url, producer, product_description, title) VALUES (null, 526.00, 129, null, 'ABSENT', 'https://images.prom.ua/Ut8hWtZr15rqUi05vY0S4Wt2o2MXJMELyJyKxBKr_ws=/2984847941_paketi-dlya-sterilizatsiyi.jpg', 'Медипак', 'Для виробництва рулонів і пакетів для стерилізації застосовуються тільки високоякісна сировина провідних виробників Європи та США, а саме спеціальний медичний багатошаровий вологостійкий безворсовий папір, що володіє підвищеною міцністю, спеціальна медична багатошарова плівка, що володіє високими бар''єрними властивостями, стійка до високих температур, спеціальний медичний скотч для упаковки пакетів зі скотчем з термостійким клеєм, а також високоякісні індикатори міток на упаковку для контролю процесу стерилізації. Сучасний дизайн упаковки відповідає високим вимогам, що пред''являються до стерилізації медичних виробів та інструментів. Суцільний посилений термошов забезпечує збереження шва і надійну герметичність. Кольорові індикатори забезпечують контроль якості процесу стерилізації. Рулони і пакети для стерилізації пройшли технічні випробування, доклінічні санітарно-гігієнічні, токсиколого-гігієнічні, медико-біологічні випробування, випробування на біосумісність у відповідній акредитованій лабораторії, а також клінічні випробування і має медичний сертифікат, виданий органом з оцінки відповідності "Українським медичним центром сертифікації" МОЗ України, а також висновок санітарно-епідеміологічної експертизи. Продукція внесена до державного реєстру медичної техніки та виробів медичного призначення', 'Пакети для стерилізації у рулонах з складкою OPTIMALITY 100х50х100');
INSERT INTO products (opt_price, price, id, country_producer, on_stock, photo_url, producer, product_description, title) VALUES (null, 950.00, 215, null, 'ABSENT', 'https://images.prom.ua/rA3ckx0vnipf6Q-4q3j0gl_NBP4OHlb599m075SRKRI=/4622195111_vindez-chas-am-5l.jpg', 'Ама', '«Віндез ЧАС-АМ» – дезінфекційний засіб широкого спектра дії. Форма випуску: концентрований засіб у формі прозорої рідини, від безбарвної до світло-жовтого кольору, яка добре змішується з водою у будь-яких співвідношеннях. Склад засобу: N,N-біс(3-амінопропил)додециламін 10,0%, алкілдиметилбензиламонію хлорид 6,5%; поверхнево-активні речовини, інгібітор корозії, регулятор pH, стабілізатор, комплексоутворювач, вода до 100,0%. Антимікробна складова: 6,5% четвертинної амонієвої сполуки, 10% триаміну. Властивості: водні розчини засобу прозорі, майже безбарвні, майже не мають запаху, стійкі; забезпечують ефективну дезінфекцію широкого спектра об’єктів; мають пролонговану антимікробну дію, відмінні змочувальні та миючі властивості, усувають неприємні запахи; не пошкоджують об’єкти обробки; не фіксують органічні та неорганічні забруднення на об’єктах обробки; добре змиваються, не залишають плям і нальоту; гомогенізують мокротиння та інші виділення. Засіб незаймистий, вибухобезпечний. Призначення: • профілактична, поточна та заключна дезінфекція, поточні та генеральні прибирання в установах, організаціях і на підприємствах різного профілю, а також у побуті; • дезінфекція поверхонь приміщень, повітря у приміщеннях, меблів, медичного обладнання (у т.ч. особливо чутливої апаратури, кувезів), технологічного обладнання, санітарно-технічного обладнання, транспортних засобів; • дезінфекція кухонного та столового посуду, ємностей для води, систем подачі води; • дезінфекція білизни, одягу, взуття, іграшок, спортивного інвентарю, прибирального інвентарю; • дезінфекція предметів догляду хворих, засобів особистої гігієни; • дезінфекція, достерилізаційне очищення, стерилізація виробів медичного призначення (у т.ч. гну