INSERT INTO products (opt_price, price, id, country_producer, on_stock, photo_url, producer, product_description, title) VALUES (null, 785.00, 212, null, 'ON_STOCK', 'https://images.prom.ua/pCSP973FgfcseyVA19NpfRsAqzhWUxge6nQebk3sCKU=/4224092757_vinsept-ekspres-5l.jpg', null, 'Дезінфекційний засіб «Вінсепт Експрес» Технічні характеристики Тип препарату: Дезінфектанти Склад: спирт етиловий, спирт ізопропіловий , 2-феноксіетанол, вода Форма випуску: готовий до застосування спиртовий засіб у формі прозорої рідини. Сфера використання: HoReCa, Ветеринарія, Медицина, Харчова промисловість Переваги: Швидка дезінфекція 15 секунд Ефективний: широкий спектр дії Етилова основа Не містить ароматизаторів та барвників Зручний, не потребує змивання Безпечний для здоров’я та навколишнього середовища (IV клас малонебезпечних речовин) Опис: Вінсепт Експрес — призначений для швидкої дезінфекції поверхонь приміщень, предметів обстановки, обладнання, медичних, косметологічних інструментів та інших об’єктів, а також антисептичної обробки шкіри рук та тіла в ургентних ситуаціях. Засіб має державну реєстрацію та відповідні дозволи в Україні.', 'Вінсепт Експрес 5л');
INSERT INTO products (opt_price, price, id, country_producer, on_stock, photo_url, producer, product_description, title) VALUES (null, 710.00, 217, null, 'ON_STOCK', 'https://images.prom.ua/VzemR7eIdtimWrB94L1MAmC7wzpzUmgNZA3pgTkqDK8=/4622230157_vindez-chas-s-1l.jpg', null, '«Віндез ЧАС-С» – дезінфекційний засіб широкого спектра дії. Форма випуску: концентрований засіб у формі прозорої рідини, від блакитного до синього кольору, яка добре змішується з водою у будь-яких співвідношеннях. Склад засобу: дидецилдиметиламонію хлорид 6,1%, спирт ізопропіловий 5,0%; поверхнево-активні речовини, інгібітор корозії, регулятор pH, стабілізатор, комплексоутворювач, вода до 100,0%. Антимікробна складова: 6,1% четвертинної амонієвої сполуки, 5% спирту. Властивості: водні розчини засобу прозорі, від майже безбарвних до світло-блакитного кольору, майже не мають запаху, стійкі; забезпечують ефективну дезінфекцію широкого спектра об’єктів; мають пролонговану антимікробну дію, відмінні змочувальні та миючі властивості, усувають неприємні запахи; не пошкоджують об’єкти обробки з матеріалів, стійких до дії низькоконцентрованих спиртових розчинів; не фіксують органічні та неорганічні забруднення на об’єктах обробки; добре змиваються, не залишають плям і нальоту; гомогенізують мокротиння та інші виділення. Призначення: • профілактична, поточна та заключна дезінфекція, поточні та генеральні прибирання в установах, організаціях і на підприємствах різного профілю, а також у побуті; • дезінфекція поверхонь приміщень, повітря у приміщеннях, меблів, медичного обладнання (у т.ч. особливо чутливої апаратури, кувезів), технологічного обладнання, санітарно-технічного обладнання, транспортних засобів; • дезінфекція кухонного та столового посуду, ємностей для води, систем подачі води; • дезінфекція білизни, одягу, взуття, іграшок, спортивного інвентарю, прибирального інвентарю; • дезінфекція предметів догляду хворих, засобів особистої гігієни; • дезінфекція, достерилізаційне очищення, стерилізація виробів медичного призначення (у т.ч. гнучких і жорстких ендоскопів, інструментів до них), перукарського, косметологічного, манікюрного, педикюрного інструментарію, аптечного та лабораторного посуду і скла; • дезінфекція високого рівня гнучких і жорстких ендоскопів, інструментів до них; • дезінфекція медичних відходів, відпрацьованого матеріалу; • знезараження біологічних та інших контамінованих відходів; • заповнення дезінфекційних бар’єрів; • обробка поверхонь з метою знищення та попередження появи плісняви. Сфери застосування: заклади охорони здоров’я, лікувально-профілактичні заклади, вогнища інфекційних захворювань, аптечні, дитячі, навчально-виховні, соціального захисту, геріатричні заклади, місця постійного та тимчасового проживання, офіси, спортивно-оздоровчі комплекси, басейни, аквапарки, сауни, лазні, об’єкти комунально-побутового призначення, адміністративні та громадські заклади, банківські установи, заклади зв’язку, культури та відпочинку, готельного та ресторанного господарства, торгівлі, місця масового скупчення людей, громадські туалети, підприємства фармацевтичної, мікробіологічної, парфюмерно-косметичної, хімічної, біотехнологічної, харчопереробної промисловості, цукрової та спиртової галузей, водопостачання та водовідведення, агропромислового комплексу, промислові підприємства, санпропускники, склади та сховища, підприємства з переробки сміття, рухомий склад та об’єкти забезпечення автомобільного транспорту, метрополітену, залізничного, водного, повітряного транспорту, об’єкти міністерств, служб і відомств, митниці та прикордонних служб, установ пенітенціарної системи, інші епідемічно значимі об’єкти, діяльність яких вимагає проведення дезінфекції. Переваги: Широкий спектр антимікробної дії: • бактерії (у т.ч. мікобактерії туберкульозу, кишкова та синьогнойна палички); • віруси (у т.ч. віруси гепатитів, ВІЛ-інфекції, коронавірусної інфекції, грипу, «пташиного грипу», «свинячого грипу», SARS); • грибки (збудники кандидозів, дерматомікозів, трихофітій);